import axios from 'axios';
import { FETCH_POSTS } from './type';

export const fetchPosts = () => async dispatch => {
  return dispatch => {
    axios.get(`http://localhost:8000/api/main`)
      .then(response => {
        const users = response.data;
        dispatch({
          type: FETCH_POSTS,
          payload: users
        });
      })
  };
  };
