import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { fetchContents, fetchPosts } from "../redux/actions/postActions";
import Maps from "../component/maps";
import '../css/Main.css';

const PostList = () => {
    const dispatch = useDispatch();
    const posts = useSelector(state => state.posts.posts);
    const contents = useSelector(state => state.contents.contents);
    const [curIdx, setCurIdx] = useState(0);    //현재 슬라이드의 인덱스

    useEffect(() => {
        dispatch(fetchPosts());
        dispatch(fetchContents());
    }, [dispatch]);

    useEffect(() => {
        // 콘텐츠가 변경될 때마다 curIdx를 초기화
        setCurIdx(0);
    }, [contents]);

    const handleNext = (filteredContentsLength) => {
        setCurIdx((prevIdx) => (prevIdx + 1) % filteredContentsLength);
    }

    const handlePrev = (filteredContentsLength) => {
        setCurIdx((prevIdx) => (prevIdx - 1 + filteredContentsLength) % filteredContentsLength);
    }

    return (
        <React.Fragment>
            {posts.map((post) => {
                const filteredContents = contents.filter(content => content.post_id === post.post_id).reverse();
                if (filteredContents.length === 0) {
                    return null; // 필터링된 콘텐츠가 없으면 아무것도 렌더링하지 않음
                }

                const currentContent = filteredContents[curIdx % filteredContents.length];

                return (
                    <div key={post.post_id}>
                        <h3>{post.title}</h3>
                        <p>{post.user_name}</p>
                        <button onClick={() => handlePrev(filteredContents.length)} className="swipe_bt">{"<"}</button>
                        {currentContent && (
                            <div key={currentContent.id}>
                                <img
                                    src={currentContent.img_src}
                                    style={{ width: '20%', height: '40vh' }}
                                />
                                {currentContent.img_src}
                                {console.log(currentContent.img_src)}
                                <div style={{ width: '30%', height: '400px' }}>
                                    <Maps lat={currentContent.lat} lon={currentContent.lon} />
                                </div>
                                <p>{currentContent.content}</p>
                            </div>
                        )}
                        <button onClick={() => handleNext(filteredContents.length)} className="swipe_bt">{">"}</button>
                    </div>
                );
            })}
        </React.Fragment>
    );
}

export default PostList;
