import { useEffect, useState, useContext, useMemo } from "react";
import { DeletePlan } from "./handlePlan";
import { useDispatch, useSelector } from "react-redux";
import { fetchTitles } from "../redux/actions/listActions";
import { AuthContext } from '../context/AuthContext';

const PlanBanner = ({ positions }) => {
    const [position, setPosition] = useState(positions);
    const dispatch = useDispatch();
    const titles = useSelector((state) => state.titles.titles);//titles까지 불러왔으니 이제 이걸 멤버에 맞게 필터링하고 그걸 다시 드롭다운으로 표시해주고 planinfo쪽 수정해주면 됨.
    const { isAuthenticated, userInfo } = useContext(AuthContext);
    const [view, setView] = useState(false);
    const [initTitle, setInitTitle] = useState("일정을 선택해주세요");

    const titleList = useMemo(()=>{     //useEffect에 객체 전달로 인한 리랜더링 방지, user_content_title객체
        if(isAuthenticated && userInfo){
            return titles.filter(title => title._user_id === userInfo[0].user_id);
        }
        return [];
    }, [titles, isAuthenticated, userInfo]);

    useEffect(() => {
        dispatch(fetchTitles());
    }, [dispatch]);

    useEffect(() => {
        setPosition(positions);
    }, [positions]);

    const handleDeletePlan = async (list_id) => {
        try {
            await DeletePlan(list_id);
            setPosition(prevPosition => prevPosition.filter(position => position.list_id !== list_id));
        } catch (error) {
            alert('Failed to delete plan.');
        }
    }

    const drop = () => {
        return titleList.map(tL => (
            <li className="title_li" onClick={()=>setInitTitle(tL.user_content_title)}>{tL.user_content_title}</li>
        ))
    }

    return (
        <div className="banner_wrap">
            {position.length > 0 && (
                <>
                <ul className="banner_title" onClick={() => setView(!view)}>
                    <p className="down">▼</p>
                    {initTitle}
                    {view && drop()}
                </ul>
                </>
            )}
            {position.length > 0 && (
                position.map(pos => (
                    <div key={pos.list_id} className="each_hour">
                        <div className="time_wrap">
                            <p className="start_hour">{`${pos.start_time}시`}</p>
                            <p className="end_hour">{`${pos.end_time}시`}</p>
                        </div>
                        <div className="middle_wrap">
                            <img src={pos.category} className="cate_img" />
                            <p className="loc_name">{pos.name}</p>
                        </div>
                        <button className="delete_bt" onClick={() => handleDeletePlan(position.list_id)}>X</button>
                    </div>
                ))
            )}
        </div>
    )
}

export default PlanBanner;
