import axios from "axios";
import React, { useEffect, useState } from "react";

const Review = ({place_id}) => {
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [sum, setSum] = useState(0);
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const fetchPlaceData = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`http://localhost:8000/api/reviews/${place_id}`);
                setReviews(response.data);
            } catch (err) {
                console.log(err.message);
            } finally {
                setLoading(false);
            }
        };

        if (place_id) {
            fetchPlaceData();
        }
    }, [place_id]);

    useEffect(() => {
        const ratingAvg = () => {
            var sum = 0;
            reviews.map((rev) => {
                sum += rev.rating;
            });
            sum = sum / reviews.length;
            setSum(sum);
        }

        if(reviews) {
            ratingAvg();
        }
    }, [reviews])

    const handleNext = (review) => {
        if (currentIndex === review.img.length - 3) {
            setCurrentIndex(currentIndex + 0.5);
        }
        else if (currentIndex < review.img.length - 3) {
            setCurrentIndex(currentIndex + 1);
        }
    };

    const handlePrev = (review) => {
        if (currentIndex > 0) {
            setCurrentIndex(currentIndex - 1);
        }
    };

console.log(reviews);
    return (
        <div className="place_review">
            <div className="rating">
                <p className="review_rating">⭐ {sum}</p>
                <p className="review_count">{reviews.length}개의 리뷰</p>
            </div>
            <div className="review_wrap">
                {reviews.slice(0, 3).map((review) => (
                    <div className="review">
                        <div className="review_top">
                            <img src={review.profile_img} className="review_profile_img" />
                            <p>{review.name}</p>
                            <p className="personal_rating">⭐ {review.rating}</p>
                        </div>
                        <div className="review_body">
                            <div className="review_img_wrap">
                                {review.img.map((img, index) => (
                                    <img
                                        src={img} className="review_img"
                                        style={{ transform: `translateX(-${currentIndex * 100}%)`, transform: `translateX(-${Math.round(currentIndex) * 100}%)` }}
                                        key={index}
                                    />
                                ))}
                            </div>
                            <p className="review_commit">{review.comment}</p>
                            <div className="navigation">
                                <button onClick={() => handlePrev(review)} disabled={currentIndex === 0}>
                                    이전
                                </button>
                                <button onClick={() => handleNext(review)} disabled={currentIndex === review.img.length - 1}>
                                    다음
                                </button>
                            </div>
                        </div>
                    </div>

                ))}
            </div>
        </div>
    )
}

export default Review;
