import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { fetchContents, fetchPosts } from "../redux/actions/postActions";
import Maps from "../component/maps";
import 'swiper/css';
import '../css/Main.css';

const Main = () => {
    const dispatch = useDispatch();
    const posts = useSelector(state => state.posts.posts);
    const contents = useSelector(state => state.contents.contents);
    const [curIdx, setCurIdx] = useState(0);    //현재 슬라이드의 인덱스

    useEffect(() => {
        dispatch(fetchPosts());
        dispatch(fetchContents());
    }, [dispatch]);

    const handleNext = () => {
        setCurIdx((prevIdx) => (prevIdx + 1) % contents.length);
    }

    const handlePrev = () => {
        setCurIdx((prevIdx) => (prevIdx - 1 + contents.length) % contents.length);
    }

    return (
        <React.Fragment>
            <div className="post_list">
                {posts.map((post) => (  //게시글의 정보를 가져와 차례로 로드함
                    <div key={post.post_id}>
                        <h3>{post.title}</h3>
                        <p>{post.user_name}</p>
                        <button onClick={handlePrev} className="swipe_bt">{"<"}</button>
                        {contents.filter(content => content.post_id === post.post_id)   //게시글에 맞는 내용을 필터링
                                .slice(curIdx, cur)
                                .map(content => (
                                    <div key={content.id}>
                                        <img
                                            src={content.img_src}
                                            style={{ width: '20%', height: '40vh' }}
                                        />
                                        <div style={{ width: '30%', height: '400px' }}>
                                            <Maps lat={content.lat} lon={content.lon} />
                                        </div>
                                        <p>{content.content}</p>
                                    </div>
                                ))
                            }
                        <button onClick={handleNext} className="swipe_bt">{">"}</button>

                    </div>
                ))}

            </div>
        </React.Fragment>
    )

}

export default Main;
