import axios from "axios";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUsers } from "../../redux/actions/userActions";

const Review = ({place_id}) => {
    const dispatch = useDispatch();
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [sum, setSum] = useState(0);
    const [userInfo, setUserInfo] = useState(null);
    const userlists = useSelector(state => state.users.users);
    
    useEffect(() => {
        dispatch(fetchUsers());
    }, [dispatch]);

    useEffect(() => {
        const fetchPlaceData = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`http://localhost:8000/api/reviews/${place_id}`);
                setReviews(response.data);
            } catch (err) {
                console.log(err.message);
            } finally {
                setLoading(false);
            }
        };

        if (place_id) {
            fetchPlaceData();
        }
    }, [place_id]);

    useEffect(() => {
        const ratingAvg = () => {
            var sum = 0;
            reviews.map((rev) => {
                sum += rev.rating;
            });
            sum = sum / reviews.length;
            setSum(sum);
        }

        if(reviews) {
            ratingAvg();
        }
    }, [reviews])

    useEffect(() => {
        const filterUsers = () => {
            reviews.map()
        }
    }, [reviews]);

    const handleUser = (user_id) => {
        const user = userlists.filter(user => user.user_id === user_id);
        setUserInfo(user);
        return (
            <>
            {userInfo && (
                <div><img src={userInfo.profile_img} /></div>
            )}
            </>
        )
    }

console.log(userlists);
    return (
        <div className="place_review">
            <div className="rating">
                <p className="review_rating">⭐ {sum}</p>
                <p className="review_count">{reviews.length}개의 리뷰</p>
            </div>
            {reviews.slice(0,3).map((review) => {
                <div className="review_wrap">
                    <div className="review_top">
                        <p></p>
                    </div>
                </div>

            })}
        </div>
    )
}

export default Review;
