import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

import "../css/signUp.css";

const SignUp = () => {
    const navigate = useNavigate();
    const [loginData, setLoginData] = useState({
        username: '',
        password: '',
    });

    const change = (e) => {
        setLoginData({ ...loginData, [e.target.name]: e.target.value });
    }

    const checkUser = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8000/api/login', loginData, {
            withCredentials: true
        })
        .then((response) => {
            if (response.status === 200) {
                alert(`환영합니다. ${loginData.username}님`);
                navigate('/main');
            }
        })
        .catch((error) => {
            alert("아이디 또는 비밀번호를 확인해주세요.");
            console.error(error);
        });
    }

    return(
        <div className="signup_wrap">
            <form onSubmit={checkUser}>
                <h2>회원가입</h2>
                <div className="signup_form">
                    <p><input
                        className="signup id"
                        type="text" name="username"
                        placeholder="아이디를 입력해주세요."
                        value={loginData.username}
                        onChange={change}
                    /></p>
                    <p><input
                        className="signup pw"
                        type="password" name="password"
                        placeholder="비밀번호를 입력해주세요."
                        value={loginData.password}
                        onChange={change}
                    /></p>
                </div>
                <div className="signup_bt_wrap">
                    <input className="bt login_bt" type="submit" value="로그인" />
                    <input className="bt sign_up" type="button" value="회원가입" onClick={navigate('/sign_up')}/>
                </div>
            </form>
        </div>
    );
}

export default SignUp;
