import React, { useEffect, useMemo, useRef, useState } from "react";
import { Swiper, SwiperSlide } from 'swiper/react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchContents, fetchPosts } from "../redux/actions/postActions";
import { Navigation } from 'swiper/modules';
import { useNavigate } from "react-router-dom";
import { AddPlan } from "./handlePlan";
import fetchLocations from "./fetchLoc";
import useFetchMaps from "./fetchMaps";
import '../css/postList.css';
import 'swiper/css';
import 'swiper/css/navigation';

const { kakao } = window;

const PostList = ({ post }) => {//분리 시도는 좋았으나, 
    const mapContainerRef = useRef(null);
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const contents = useSelector(state => state.contents.contents);
    const content = useMemo(() => contents.filter(c => c.post_id === post.post_id), [contents, post.post_id]);
    const [loading, setLoading] = useState(true);
    const [locations, setLocations] = useState([]);
    const [selected, setSelected] = useState(0);
    const [swiperInstance, setSwiperInstance] = useState(null);
    const { option, markers } = useFetchMaps({ content });

    //console.log(mapContainerRef.current);
    useEffect(() => {
        const fetch = () => {
            dispatch(fetchContents());
            setLoading(false);
        };
        fetch();
    }, [dispatch]);

    useEffect(() => {
        const fetchLoc = async () => {
            if (contents.length > 0) {
                const locs = await fetchLocations(content);
                setLocations(locs);
            }
        };
        fetchLoc();
    }, [content]);

    useEffect(() => {
        const initializeMap = () => {
            if (mapContainerRef.current && option.center && markers.length > 0) {
                //console.log(mapContainerRef.current);
                const map = new kakao.maps.Map(mapContainerRef.current, option);
                markers.forEach(marker => marker.setMap(map));
            }
        };
        if (mapContainerRef.current) {
            initializeMap();
        }
    }, [option, markers, mapContainerRef.current]);

    const handleAddPlan = async ({ content }) => {
        try {
            await AddPlan({ content });
        } catch (error) {
            alert('Failed to add plan.');
        }
    };

    const navigateSlide = (postId, index) => {
        if (swiperInstance) {
            swiperInstance[postId].slideTo(index);
        }
        setSelected(index);
    }

    const navigatePage2User = (user_id) => {
        navigate(`/profile/${user_id}`);
    }

    const navigatePage2Detail = (post_id) => {
        navigate(`/post/${post_id}`);
    }

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <React.Fragment>
            <div className="post_wrap">
                <div className="content_wrap">
                    <div className="post_top" onClick={() => navigatePage2User(post.user_id)}>
                        <img src={post.profile_img} />
                        <div className="post_user_info">
                            <p className="info_id">{post.username}</p>
                            <p>{post.name}</p>
                        </div>
                    </div>
                    <Swiper
                        spaceBetween={20}
                        slidesPerView={1}
                        navigation
                        pagination={{
                            type: "bullets"
                        }}
                        onSwiper={(swiper) => setSwiperInstance(prev => ({ ...prev, [post.post_id]: swiper }))}
                        modules={[Navigation]}
                    >
                        {content.map((con) => (
                                <SwiperSlide key={con.id}>
                                    <div className="silde_frame" onClick={() => { navigatePage2Detail(con.post_id) }}>
                                        <div className="post_content">
                                            {con.content}
                                        </div>
                                        <img
                                            src={con.img_src}
                                        />
                                        <div className="map_wrap">
                                            <div className="main_map" ref={mapContainerRef} ></div>
                                        </div>
                                    </div>
                                    <button className="add_plan" onClick={() => handleAddPlan({ con })}>추가하기</button>
                                </SwiperSlide>
                            ))
                        }
                    </Swiper>
                </div>
                <div className="post_banner_wrap">
                    <p className="post_banner_title">{post.title}</p>
                    {content.map((con, index) => (
                            <>
                                <div
                                    key={con.content_id}
                                    className={`post_each_hour ${selected === con.content_id ? "selected" : ""}`}
                                    onClick={() => navigateSlide(post.post_id, index)}
                                >
                                    <div className="post_banner_num">{index + 1}</div>
                                    <div className="post_middle_wrap">
                                        {locations[index] && (
                                            <p className="post_loc_name">{con.name}</p>
                                        )}
                                    </div>
                                </div>
                                <div className="post_banner_sep">▼</div>
                            </>
                        ))
                    }
                </div>
            </div>
        </React.Fragment>
    )

}

export default PostList;
