import React, { useContext, useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";

import "../css/ChatRoom.css";
import { useDispatch, useSelector } from "react-redux";
import { fetchUsers } from "../redux/actions/userActions";

const ChatRoom = () => {
    const navigate = useNavigate();
    const { user_id } = useParams();
    const { loading, userInfo } = useContext(AuthContext);
    const [chatLog, setChatLog] = useState([]);
    const [message, setMessage] = useState('');
    const sort_id = [user_id, userInfo.user_id].sort();
    const roomId = sort_id.join('_');
    const [roomExist, setRoomExist] = useState(false);
    const dispatch = useDispatch();
    const users = useSelector(state => state.users.users);
    const target_info = users.find(user => user.user_id === Number(user_id));
    
    useEffect(() => {
        dispatch(fetchUsers());
    }, [dispatch]);

    useEffect(() => {
        const findRoom = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/chat_room', {
                    params: {roomId}
                });

                if (response.data.exists) {
                    setRoomExist(true);
                    setChatLog(response.data.chatLogs);  // 채팅 로그 불러오기
                } else {
                    setRoomExist(false);
                }
            } catch (err) {
                console.log(err);
            }
        };

        if(loading) {
            findRoom();
        }
    }, [loading, roomId]);
//console.log(target_info);
    return (
        <React.Fragment>
            <div className="chat_room_wrap">
                <div className="chat_room_top">
                    <div className="chat_room_close" onClick={() => navigate(-1)}>⇦</div>
                    <div className="target_user">{target_info ? target_info.name : "Loading..."}</div>
                </div>
                <div className="chat_room_middle">
                    
                </div>
                <div className="chat_room_bottom">
                    <input className="textbox" type="text" placeholder="메시지를 입력해주세요." />
                    <button className="send_message" type="subbit">전송</button>
                </div>
            </div>
        </React.Fragment>
    )
}

export default ChatRoom;
