import axios from 'axios';
import { FETCH_PC } from './type';

export const fetchPC = (post_id) => {
  return dispatch => {
    axios.get(`http://localhost:8000/api/PC/${post_id}`)
      .then(response => {
        const PC = response.data;
        dispatch({
          type: FETCH_PC,
          payload: PC
        });
      })
  };
};
