import React, { useEffect, useRef, useState } from "react";
import List from "./list";
import "../css/list_map.css";

const { kakao } = window;

const ListMap = ({ positions }) => {
    const mapContainerRef = useRef(null);
    const [viewPoint, setViewPoint] = useState(null);

    useEffect(() => {
        if (positions.length > 0) {
            initializeMap(positions);
        }
    }, [positions]);

    const initializeMap = (positions) => {
        if (mapContainerRef.current && positions.length > 0) {
            const container = mapContainerRef.current;
            const options = {
                center: positions[0].latlng,
                level: 4
            };
            const newMap = new kakao.maps.Map(container, options);

            positions.forEach((position) => {
                const marker = new kakao.maps.Marker({
                    position: position.latlng,
                    clickable: true
                });
                marker.setMap(newMap);
            });

            if(viewPoint !== null){
                map.panTo(viewPoint);
            }
        }
    };

    const viewPlan = ( index ) => {
        console.log(positions[index].latlng, index);
        setViewPoint(positions[index].latlng);
    }

    return (
        <React.Fragment>
            <div className="map" ref={mapContainerRef}></div>
            <div className="list_wrap">
                {positions.length > 0 && (<p>{positions[0].title}</p>)}
                {positions.map((position, index) => (
                    <div key={index}>
                        <button onClick={() => viewPlan(index)}>{position.name}</button>
                        <button onClick={() => viewPlan(index)}>{position.memo}</button>
                    </div>
                ))}
            </div>
        </React.Fragment>
    );
};

export default ListMap;
