import React, { useEffect } from "react";
import { Swiper, SwiperSlide } from 'swiper/react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchContents, fetchPosts } from "../redux/actions/postActions";
import Maps from "../component/maps";
import 'swiper/css';
import '../css/Main.css';

const Main = () => {
    const dispatch = useDispatch();
    const posts = useSelector(state => state.posts.posts);
    const contents = useSelector(state => state.contents.contents);

    useEffect(() => {
        dispatch(fetchPosts());
        dispatch(fetchContents());
    }, [dispatch]);

    return (
        <React.Fragment>
            <div className="post_list">
                {posts.map((post) => (  //게시글의 정보를 가져와 차례로 로드함
                    <div key={post.post_id}>
                        <h3>{post.title}</h3>
                        <p>{post.user_name}</p>
                        <button className="swiper-prev swiper_bt">{"<"}</button>
                        
                        <Swiper
                            spaceBetween={20}
                            slidesPerView={1}
                            navigation={{
                                prevEl: ".swiper-prev",
                                nextEl: ".swiper-next"
                            }}
                        >
                            {contents.filter(content => content.post_id === post.post_id)   //게시글에 맞는 내용을 필터링
                                .reverse()
                                .map(content => (   
                                    <SwiperSlide className="silde" key={content.id}>
                                        <img 
                                            src={content.img_src} 
                                            style={{ width: '20%', height: '40vh' }}
                                        />
                                        <div style={{ width: '30%', height: '400px' }}>
                                            <Maps />
                                        </div>
                                        <p>{content.content}</p>            
                                    </SwiperSlide>
                                    
                                ))
                            }
                        </Swiper>
                        <button className="swiper-next swiper_bt">{">"}</button>

                    </div>
                ))}

            </div>
        </React.Fragment>
    )

}

export default Main;
