import React, { useEffect, useRef, useState } from "react";
import fetchLocations from "./fetchLoc";
import "../css/list_map.css";

const { kakao } = window;

const Maps = ({ content }) => {
    const mapContainerRef = useRef(null);  // useRef를 사용하여 DOM 요소를 참조
    const [locations, setLocations] = useState(null);  // 위치 상태를 추가
    const [map, setMap] = useState({});

    useEffect(() => {
        const fetchLoc = async () => {
            if (content) {
                const locs = await fetchLocations([content]);
                if (locs) {
                    setLocations(locs[0]);
                }
            }
        };

        fetchLoc();
    }, [content]);

    useEffect(() => {
        if (locations) {
            maps();
        }
    }, [locations]);  // lat과 lon이 변경될 때마다 effect 실행

    const maps = () => {
        if (mapContainerRef.current && locations) {  // mapContainerRef.current가 유효할 때만 실행
            const container = mapContainerRef.current;
            const options = {
                center: locations[0].latlng,
                level: 4
            };
            var map = new kakao.maps.Map(container, options);

            locations.forEach((loc) => {
                const marker = new kakao.maps.Marker({
                    position: loc.latlng,
                    clickable: true
                });
                marker.setMap(map);
            });
            setMap(map);

            /*
            locations.forEach((loc) => {
                var marker = new kakao.maps.Marker({
                    position: loc.latlng,
                    clickable: true
                });
                // 마커가 지도 위에 표시되도록 설정합니다
                marker.setMap(map);
            });
            
            setMapIns(map);
            */

            /*kakao.maps.event.addListener(map, 'tilesloaded', function() {
                // 현재 지도의 중심 좌표 가져오기
                var center = map.getCenter();
            
                // 새로운 중심 좌표를 계산 (오른쪽으로 20% 이동)
                var moveLatLon = new kakao.maps.LatLng(center.getLat(), center.getLng() - (map.getBounds().getSouthWest().getLng() - center.getLng()) * 0.3);
            
                // 애니메이션 없이 바로 중심을 설정
                if(post){
                    map.setCenter(moveLatLon);
                    map.setZoomable(false);
                    map.setDraggable(false);
                }
            });*/
        }
    }

    return map;  // ref를 div에 설정
};

export default Maps;
