import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import "../css/Chat.css";

// 서버와 소켓 연결
const socket = io('http://localhost:8000/');

const Chat = ({ userId, targetUserId }) => {
  const [message, setMessage] = useState('');
  const [chatLog, setChatLog] = useState([]);
  const [roomId, setRoomId] = useState('');

  useEffect(() => {
    // 유저 간의 고유한 채팅방 ID 설정
    const newRoomId = [userId, targetUserId].sort().join('_');
    setRoomId(newRoomId);

    // 서버로 방 참여 요청
    socket.emit('joinRoom', { userId, targetUserId });

    // 메시지 수신
    socket.on('receiveMessage', (receivedMessage) => {
      setChatLog((prevChatLog) => [...prevChatLog, receivedMessage]);
    });

    return () => {
      socket.off('receiveMessage'); // 컴포넌트 언마운트 시 이벤트 해제
    };
  }, [userId, targetUserId]);

  // 메시지 전송
  const sendMessage = () => {
    if (message.trim()) {
      // 특정 방으로 메시지 전송
      socket.emit('sendMessage', { roomId, message });
      setMessage(''); // 메시지 입력 필드 초기화
    }
  };

  return (
    <div className="chat_wrap">
      <h2>{targetUserId}님과의 채팅</h2>
      <div className="chat_log">
        {chatLog.map((msg, index) => (
          <div key={index}>{msg}</div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="메시지를 입력하세요..."
      />
      <button onClick={sendMessage}>전송</button>
    </div>
  );
};

export default Chat;
