import React, { useEffect } from "react";
import { Swiper, SwiperSlide } from 'swiper/react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchContents, fetchPosts } from "../redux/actions/postActions";
import Maps from "../component/maps";
import PlanBanner from "./banner";
import { AddPlan } from "./handlePlan";
import '../css/postList.css';
import 'swiper/css';

const PostList = ({ positions }) => {
    const dispatch = useDispatch();
    const posts = useSelector(state => state.posts.posts);
    const contents = useSelector(state => state.contents.contents);
    const [isBeginning, setIsBeginning] = useState(true);
    const [isEnd, setIsEnd] = useState(false);

    useEffect(() => {
        dispatch(fetchPosts());
        dispatch(fetchContents());
    }, [dispatch]);

    const handleAddPlan = async ({ content }) => {
        try {
            await AddPlan({ content });
        } catch (error) {
            alert('Failed to add plan.');
        }
    };

    const handlePrev = () => {
        swiper?.slidePrev();
    };

    const handleNext = () => {
        swiper?.slideNext();
    };

    return (
        <React.Fragment>
            <PlanBanner positions={positions} />
            <div className="main_wrap">
                {posts.map((post) => (
                    <div key={post.post_id}>
                        <h3>{post.title}</h3>
                        <p>{post.user_name}</p>
                        <button className="swiper-prev swiper_bt" disabled={isBeginning} handleClick={handlePrev} direction='left'>{"<"}</button>
                        <Swiper
                            spaceBetween={20}
                            slidesPerView={1}
                            navigation={{
                                prevEl: ".main_wrap .swiper-prev",
                                nextEl: ".main_wrap .swiper-next"
                            }}
                            onSlideChange={(e) => {
                                setIsBeginning(e.isBeginning);
                                setIsEnd(e.isEnd);
                            }}
                            onSwiper={(e) => {
                                setSwiper(e);
                            }}
                        >
                            {contents.filter(content => content.post_id === post.post_id)
                                .reverse()
                                .map(content => (
                                    <SwiperSlide key={content.id}>
                                        <div className="silde_frame">
                                            <img
                                                src={content.img_src}
                                            />
                                            <div className="map_wrap">
                                                <Maps id={`${content.id}`} address={content.address} />
                                            </div>
                                        </div>
                                        <button className="add_plan" onClick={() => handleAddPlan({ content })}>추가하기</button>
                                    </SwiperSlide>
                                ))
                            }
                        </Swiper>
                        <button className="swiper-next swiper_bt" disabled={isEnd} handleClick={handleNext} direction='right'>{">"}</button>
                    </div>
                ))}
            </div>
        </React.Fragment>
    )

}

export default PostList;
