import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../css/header.css";

const Header = ({ isAuthenticated, userInfo }) => {
    const [logged, setLogged] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (isAuthenticated) {
            setLogged(true);
        } else {
            setLogged(false);
        }
    }, [isAuthenticated]);

    const handleLoggedBanner = () => {
        if (userInfo) {
            if (userInfo.profile_img == null) {
                return (
                    <div><img src="img/기본프로필.png" /></div>
                )
            }
            else return <div><img src={userInfo.profile_img} /></div>
        }
    };

    const handleLogout = () => {
        axios.post('http://localhost:8000/api/logout', {}, { withCredentials: true })
            .then((response) => {
                alert("로그아웃 되었습니다.");
                navigate('/');
            })
            .catch((error) => {
                console.error(error);
            });
    };

    const handleLogin = () => {
        navigate('/');
    }

    return (
        <React.Fragment>
            <div className="my_banner_wrap">
                <div className="my_banner">
                    {handleLoggedBanner()}
                    {userInfo && (<div>
                        {userInfo[0].name}
                    </div>)}
                    {logged ? (
                        <button onClick={handleLogout}>로그아웃</button>
                    ) : (
                        <button onClick={handleLogin}>로그인</button>
                    )}
                </div>
            </div>
        </React.Fragment>
    )
}

export default Header;
