import React, { useEffect } from "react";
import { Swiper, SwiperSlide } from 'swiper/react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchContents, fetchPosts } from "../redux/actions/postActions";
import Maps from "../component/maps";
import 'swiper/css';
import '../css/Main.css';

const Main = () => {
    const dispatch = useDispatch();
    const posts = useSelector(state => state.posts.posts);
    const contents = useSelector(state => state.contents.contents);
    console.log(contents);
    useEffect(() => {
        dispatch(fetchPosts());
        dispatch(fetchContents());
    }, []);

    return (
        <React.Fragment>
            <div className="post_list">
                {posts.map((post) => (
                    <div key={post.post_id}>
                        <h3>{post.title}</h3>
                        <p>{post.user_name}</p>
                        <button className="swiper-prev swiper_bt">{"<"}</button>
                        
                        <Swiper
                            spaceBetween={20}
                            slidesPerView={1}
                            navigation={{
                                prevEl: ".swiper-prev",
                                nextEl: ".swiper-next"
                            }}
                        >
                            {contents.filter(content => content.post_id === post.post_id)
                                .reverse()
                                .map(content => (
                                    <SwiperSlide key={content.id}>
                                        <p>{content.content}</p>
                                        <Maps lat={content.lat} lon={content.lon} />
                                    </SwiperSlide>
                                ))
                            }
                        </Swiper>
                        <button className="swiper-next swiper_bt">{">"}</button>

                    </div>
                ))}

            </div>
        </React.Fragment>
    )

}

export default Main;
