import React from "react";

import "../css/CreatePost.css";

const CreatePostPage = () => {
    const [view, setView] = useState(false);

    const drop = () => {
        return titleList.map(tL => (
            <li
                className="title_li"
                key={tL.user_content_id}
                onClick={() => handlePlan(tL.user_content_title, tL.user_content_id)}
            >
                <button>{tL.user_content_title}</button>
            </li>
        ))
    }

    return (
        <React.Fragment>
            <div className="create_post_root">
                <div className="create_post myplan">
                    <h3>내 일정으로 만들기</h3>
                    <p>"기존의 내 일정을 선택해서 새로운 글을 작성할 수 있습니다."</p>
                    <ul
                        className={`banner_title ${view ? "clicked" : ""}`}
                        onClick={() => setView(!view)}
                    >
                        <p className="down">▼</p>
                        {initTitle}
                        {view && drop()}
                    </ul>
                </div>
                <div className="create_post newplan">
                    <h3>새 일정으로 만들기</h3>
                    <p>"새로운 일정을 생성하여 새로운 글을 작성할 수 있습니다."</p>
                </div>
            </div>
        </React.Fragment>
    )
}

export default CreatePostPage;
