import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

const { kakao } = window;

const Maps = ({ lat, lon, id }) => {
    const mapContainerRef = useRef(null);  // useRef를 사용하여 DOM 요소를 참조
    const [address, setAddress] = useState("");  // 주소 상태를 추가
    const [location, setLocation] = useState(null);  // 위치 상태를 추가
    
    const fetchAddress = async () => {
        try {
            const add_res = await axios({
                method: "GET",
                url: `https://dapi.kakao.com/v2/local/geo/coord2address.json?x=${lat}&y=${lon}&input_coord=WGS84`,
                headers: {
                    Authorization: `KakaoAK 59be56c1b793f6c69842484efa84e201`,
                },
            });
            if (add_res.data.documents.length > 0) {
                setAddress(add_res.data.documents[0].address.address_name);
            }
        } catch (error) {
            console.error("주소 변환 오류:", error);
        }
    };

    const fetchLocation = async () => {
        if (address) {
            try {
                const loc_res = await axios({
                    method: "GET",
                    url: `https://dapi.kakao.com/v2/local/search/address.json?analyze_type=similar&query=${encodeURIComponent(address)}`,
                    headers: {
                        Authorization: `KakaoAK 59be56c1b793f6c69842484efa84e201`,
                    },
                });
                if (loc_res.data.documents.length > 0) {
                    setLocation(loc_res.data.documents[0]);
                }
            } catch (error) {
                console.error("위치 검색 오류:", error);
            }
        }
    };

    //REST API를 이용해 좌표를 주소로 변환
    useEffect(() => {
        fetchAddress();
    }, [lat, lon]);

    //REST API를 이용해 주소 위치의 정보를 가져옴
    useEffect(() => {
        fetchLocation();
    }, [address]);

    useEffect(() => {
        maps();
    }, [lat, lon, location]);  // lat과 lon이 변경될 때마다 effect 실행

    const maps = () => {
        if (mapContainerRef.current) {  // mapContainerRef.current가 유효할 때만 실행
            const container = mapContainerRef.current;
            const options = {
                center: new kakao.maps.LatLng(lat, lon),
                level: 3
            };
            var map = new kakao.maps.Map(container, options);  // 카카오 지도 초기화

            var markerPosition = new kakao.maps.LatLng(lat, lon);

            // 마커를 생성합니다
            var marker = new kakao.maps.Marker({
                position: markerPosition,
                clickable: true
            });

            // 마커가 지도 위에 표시되도록 설정합니다
            marker.setMap(map);

            // 마커를 클릭했을 때 마커 위에 표시할 인포윈도우를 생성합니다
            var iwContent = '<div style="padding:5px;">Hello World!</div>'; // 인포윈도우에 표출될 내용으로 HTML 문자열이나 document element가 가능합니다
            

            // 인포윈도우를 생성합니다
            var infowindow = new kakao.maps.InfoWindow({
                content: iwContent,
                removable: true
            });

            // 마커에 클릭이벤트를 등록합니다
            kakao.maps.event.addListener(marker, 'click', function () {
                // 마커 위에 인포윈도우를 표시합니다
                infowindow.open(map, marker);
            });
        }
    }

    return <div id={id} ref={mapContainerRef} style={{ width: '100%', height: '100%' }}></div>;  // ref를 div에 설정
};

export default Maps;
