import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

const { kakao } = window;

const Maps = ({ lat, lon, id }) => {
    const mapContainerRef = useRef(null);
    const [address, setAddress] = useState("");
    const [location, setLocation] = useState(null);

    const fetchAddress = async (lat, lon) => {
        try {
            const add_res = await axios({
                method: "GET",
                url: `https://dapi.kakao.com/v2/local/geo/coord2address.json?x=${lon}&y=${lat}&input_coord=WGS84`,
                headers: {
                    Authorization: `KakaoAK YOUR_API_KEY`,
                },
            });
            if (add_res.data.documents && add_res.data.documents.length > 0) {
                setAddress(add_res.data.documents[0].address.address_name);
            } else {
                console.error("주소 변환 결과가 없습니다.");
            }
        } catch (error) {
            console.error("주소 변환 오류:", error);
        }
    };

    const fetchLocation = async (address) => {
        if (address) {
            try {
                const loc_res = await axios({
                    method: "GET",
                    url: `https://dapi.kakao.com/v2/local/search/address.json?analyze_type=similar&query=${encodeURIComponent(address)}`,
                    headers: {
                        Authorization: `KakaoAK YOUR_API_KEY`,
                    },
                });
                if (loc_res.data.documents && loc_res.data.documents.length > 0) {
                    setLocation(loc_res.data.documents[0]);
                } else {
                    console.error("위치 검색 결과가 없습니다.");
                }
            } catch (error) {
                console.error("위치 검색 오류:", error);
            }
        }
    };

    useEffect(() => {
        fetchAddress(lat, lon);
    }, [lat, lon]);

    useEffect(() => {
        if (address) {
            fetchLocation(address);
        }
    }, [address]);

    useEffect(() => {
        if (mapContainerRef.current && lat && lon) {
            const container = mapContainerRef.current;
            const options = {
                center: new kakao.maps.LatLng(lat, lon),
                level: 3
            };
            const map = new kakao.maps.Map(container, options);

            const markerPosition = new kakao.maps.LatLng(lat, lon);
            const marker = new kakao.maps.Marker({
                position: markerPosition,
                clickable: true
            });

            marker.setMap(map);

            const iwContent = `<div style="padding:5px;">${address}</div>`;
            const infowindow = new kakao.maps.InfoWindow({
                content: iwContent,
                removable: true
            });

            kakao.maps.event.addListener(marker, 'click', () => {
                infowindow.open(map, marker);
            });
        }
    }, [lat, lon, address]);

    return <div id={id} ref={mapContainerRef} style={{ width: '100%', height: '100%' }}></div>;
};

export default Maps;
