import React, { useState } from "react";
import PostList from "../component/postList";
import PlanInfo from "../component/plan_info";
import '../css/Main.css';
import "../css/mylist.css";
import Header from "../component/header";

const Main = () => {
    const [positions, setPositions] = useState([]);

    const HeaderState = () => {
        const { isAuthenticated, loading } = useContext(AuthContext);

        if (loading) {
            return null; // 로딩 중에는 헤더 랜더링 x
        }

        return <Header isAuthenticated={isAuthenticated} />
    }

    return (
        <React.Fragment>
            <div className="logged_wrap">

            </div>
            <PlanInfo setPositions={setPositions} />
            <div>
                <PostList positions={positions} />
            </div>
            <a className="my_plan" href={"/my_plan"}>마이 플랜</a>
        </React.Fragment>
    )
}

export default Main;
