import React, { useState } from "react";

const NewPost = ({ id, state }) => {
    const [upload, setUpload] = useState(true);

    return (
        <div className="create_post_wrap">
            {upload && (
                <div>
                    <input type="file" accept="image/*" />
                </div>
            )}
        </div>
    )
}

export default NewPost;
