import axios from 'axios';
import { FETCH_POSTS } from './type';

export const fetchPosts = () => {
  return async dispatch => {
    try {
        const response = await axios.get('http://localhost:8000/api/main');
        const posts = response.data;
        dispatch({
          type: FETCH_POSTS,
          payload: posts
        });
    } catch (err) {
        console.error()
    }
    
  };
};
