import { useState } from "react";
import { DeletePlan } from "./handlePlan";

const PlanBanner = ({ initPositions }) => {
    const [positions, setPositions] = useState(initPositions);
    
    const handleDeletePlan = async (list_id) => {
        try {
            await DeletePlan(list_id);
            setPositions(prevPositions => prevPositions.filter(position => position.list_id !== list_id));
        } catch (error) {
            alert('Failed to delete plan.');
        }
    }

    useEffect(() => {
        setPositions(initialPositions);
    }, [initialPositions]);

    return (
        <div className="banner_wrap">
            {positions.length > 0 && (<p>{positions[0].title}</p>)}
            {positions.reverse().map((position, index) => (
                <div key={index}>
                    <p>{position.name} {position.memo}</p>
                    <button className="delete_bt" onClick={() => handleDeletePlan(position.list_id)}>X</button>
                </div>
            ))}
        </div>
    )
}

export default PlanBanner;
