import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { fetchLists } from "../redux/actions/listActions";


const { kakao } = window;

const ListMap = () => {
    const mapContainerRef = useRef(null);  // useRef를 사용하여 DOM 요소를 참조
    const [location, setLocation] = useState([]);  // 위치 상태를 추가
    const api_key = process.env.REACT_APP_API_KEY;  //api_key .env파일 활용

    const dispatch = useDispatch();
    const lists = useSelector((state) => state.lists.lists);
    console.log(lists);

    useEffect(() => {
        dispatch(fetchLists());
    }, [dispatch]);

    useEffect(() => {
        if (lists.length > 0) {
            fetchLocations(lists);
        }
    }, [lists]);


    const fetchLocations = async (lists) => {
        const locationsPromises = lists.map(async (list) => {
            if (list.list_address) {
                try {
                    const encodedAddress = encodeURIComponent(list.list_address);
                    const loc_res = await axios({
                        method: "GET",
                        url: `https://dapi.kakao.com/v2/local/search/address.json?query=${encodedAddress}`,
                        headers: {
                            Authorization: `KakaoAK ${api_key}`,
                        },
                    });

                    if (loc_res.data.documents.length > 0) {
                        const document = loc_res.data.documents[0];
                        return { ...list, lat: document.y, lon: document.x };
                    }
                } catch (error) {
                    console.error("위치 검색 오류:", error);
                }
            }
            return null;
        });

        // 모든 비동기 작업이 완료될 때까지 기다립니다.
        const resolvedLocations = await Promise.all(locationsPromises);
        // null이 아닌 위치 데이터만 필터링합니다.
        const filteredLocations = resolvedLocations.filter(location => location !== null);
        // 필터링된 위치 데이터를 상태로 설정합니다.
        setLocation(filteredLocations);

        // 필터링된 위치 데이터가 있는 경우 지도를 초기화합니다.
        if (filteredLocations.length > 0) {
            maps(filteredLocations);
        }
    };

    const maps = (location) => {
        fetchLocations(lists);
        console.log("o");

        if (mapContainerRef.current) {  // mapContainerRef.current가 유효할 때만 실행
            const container = mapContainerRef.current;
            const options = {
                center: new kakao.maps.LatLng("34.7368325985298", "127.732888894299"),
                level: 3
            };
            var map = new kakao.maps.Map(container, options);  // 카카오 지도 초기화

            location.forEach((location) => {
                var markerPosition = new kakao.maps.LatLng(location.lat, location.lon);
                console.log(location.lat, location.lon);

                // 마커를 생성합니다
                var marker = new kakao.maps.Marker({
                    position: markerPosition,
                    clickable: true
                });

                // 마커가 지도 위에 표시되도록 설정합니다
                marker.setMap(map);
            });

        }
    }

    return <div ref={mapContainerRef} style={{ width: '100%', height: '100%' }}></div>;  // ref를 div에 설정
};

export default ListMap;
