import React, { useState } from "react";

const ImgLoader = ({ src }) => {
    const [loaded, setLoaded] = useState(false);

    const handleImgLoad = () => {
        setLoaded(true);
    };

    return (
        <div className="img_wrap">
            {!loaded && <div>Loading...</div>}
            <img
                src={src}
                onLoad={handleImgLoad}
                style={loaded ? {} : { display: "none" }}
            />
        </div>
    );
};

export default ImgLoader;
