import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { fetchLists } from "../redux/actions/listActions";

const { kakao } = window;

const ListMap = () => {
    const mapContainerRef = useRef(null);
    const [locations, setLocations] = useState([]);
    const api_key = process.env.REACT_APP_API_KEY;

    const dispatch = useDispatch();
    const lists = useSelector((state) => state.lists.lists);

    useEffect(() => {
        dispatch(fetchLists());
    }, [dispatch]);

    useEffect(() => {
        if (lists.length > 0) {
            fetchAllLocations(lists);
        }
    }, [lists]);

    const fetchAllLocations = async (lists) => {
        const locationsPromises = lists.map(async (list) => {
            if (list.list_address) {
                const encodedAddress = encodeURIComponent(list.list_address);
                try {
                    const loc_res = await axios({
                        method: "GET",
                        url: `https://dapi.kakao.com/v2/local/search/address.json?query=${encodedAddress}`,
                        headers: {
                            Authorization: `KakaoAK ${api_key}`,
                        },
                    });

                    if (loc_res.data.documents.length > 0) {
                        const document = loc_res.data.documents[0];
                        return { ...list, lat: document.y, lon: document.x };
                    }
                } catch (error) {
                    console.error("위치 검색 오류:", error);
                }
            }
            return null;
        });

        const resolvedLocations = await Promise.all(locationsPromises);
        setLocations(resolvedLocations.filter(location => location !== null));
    };

    useEffect(() => {
        if (locations.length > 0) {
            initializeMap();
        }
    }, [locations]);

    const initializeMap = () => {
        if (mapContainerRef.current) {
            const container = mapContainerRef.current;
            const options = {
                center: new kakao.maps.LatLng(locations[0].lat, locations[0].lon),
                level: 3,
            };
            const map = new kakao.maps.Map(container, options);

            locations.forEach((location) => {
                const markerPosition = new kakao.maps.LatLng(location.lat, location.lon);
                const marker = new kakao.maps.Marker({
                    position: markerPosition,
                    clickable: true,
                });
                marker.setMap(map);

                const iwContent = `<div>${location.list_name}</div>`;
                const infowindow = new kakao.maps.InfoWindow({
                    content: iwContent,
                    removable: true,
                });
                kakao.maps.event.addListener(marker, 'click', () => {
                    infowindow.open(map, marker);
                });
            });
        }
    };

    return <div ref={mapContainerRef} style={{ width: '100%', height: '100%' }}></div>;
};

export default ListMap;
